mapper.select:
SELECT BOX_CD, BOX_NM, BOX_COUNT, BOX_PRICE, DEPOSIT, ROW_COUNT, ROW_NUM, UPD_DT, UPD_ID, START_NUM, END_NUM, (SELECT COUNT(BOX_NUM) FROM ACM_BOX_NUM WHERE BOX_CD = AB.BOX_CD AND BOX_FLAG = 'Y') AS USE_CNT, (SELECT COUNT(BOX_NUM) FROM ACM_BOX_NUM WHERE BOX_CD = AB.BOX_CD AND BOX_FLAG = 'N') AS NOT_CNT FROM ACM_BOX AB
WHERE BOX_NM LIKE '%' #{searchKeyword} '%'
ORDER BY AB.BOX_CD ASC LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

mapper.insert:
INSERT INTO ACM_BOX(
	BOX_CD, BOX_NM, BOX_COUNT, BOX_PRICE, DEPOSIT, ROW_COUNT, ROW_NUM, UPD_DT, UPD_ID, START_NUM, END_NUM
)VALUES(
	#{boxCd}, #{boxNm}, #{boxCount}, #{boxPrice}, #{deposit}, #{rowCount}, #{rowNum}, now(), #{updId}, #{startNum}, #{endNum}
)

INSERT INTO ACM_BOX_NUM(
	BOX_CD, BOX_NUM, USER_ID, BOX_FLAG, UPD_DT, UPD_ID
)VALUES(
	#{boxCd}, #{boxNum}, '', 'N', now(), #{updId}
)

mapper.select:
SELECT A.ORDERNO, A.USER_ID, A.USER_NM, A.EMAIL, A.REG_DT, A.ORDER_TYPE, B.ITEMNO, B.PRICE, B.STATUSCODE, B.ISCANCEL, C.PRICE, C.PRICE_CARD, C.PRICE_CASH, C.PRICE_BANK, C.PRICE_UNPAID, C.PRICE_DISCOUNT, C.PRICE_DISCOUNT_TYPE, C.PRICE_DISCOUNT_REASON, D.BOX_NUM, D.RENT_TYPE, E.BOX_NM, E.BOX_CD, D.RENT_MEMO, D.RENT_SEQ, DATE_FORMAT(D.RENT_START, "%Y-%m-%d") RENT_START, DATE_FORMAT(D.RENT_END, "%Y-%m-%d") RENT_END FROM ACM_ORDERS A, ACM_ORDER_ITEM B, ACM_ORDER_APPROVALS C, ACM_BOX_RENT D, ACM_BOX E WHERE A.ORDERNO = B.ORDERNO AND A.ORDERNO = C.ORDERNO AND C.ORDERNO = D.ORDERNO AND D.BOX_CD = E.BOX_CD
AND B.STATUSCODE = #{statuscode}
AND A.ORDERNO = #{orderno}

mapper.update:
UPDATE ACM_BOX_NUM 
SET 
	BOX_FLAG = #{boxFlag}

	, RENT_SEQ = NULL

WHERE 
	BOX_CD = #{boxCd} AND BOX_NUM = #{boxNum}

mapper.insert:
INSERT INTO ACM_ORDER_ITEM ( 
	ORDERNO, ITEMNO, CNT, PRICE, STATUSCODE, ISCANCEL, CONFIRM_DT, MEMO,
	CONFIRMID, OPEN_ADMIN_ID, PTYPE, REG_DT, REG_ID, UPD_DT, UPD_ID		
) VALUES (
	#{orderno}, #{itemno}, 1, #{payTotal}, 'DLV105', 0, now(), #{memo},
	#{regId}, #{regId}, 'B', now(), #{regId}, now(), #{updId} 
)

INSERT INTO ACM_ORDERS (
	ORDERNO, USER_ID, USER_NM, ZIP_CODE, ADDRESS1, ADDRESS2, EMAIL, REG_DT, REG_ID
 ) SELECT  
	#{orderno}, #{userId}, USER_NM, ZIP_CODE, ADDRESS1, ADDRESS2, EMAIL, now(), #{regId}
FROM ACM_MEMBER
	WHERE USER_ID = #{userId}

INSERT INTO ACM_BOX_RENT(
	BOX_CD, BOX_NUM, RENT_SEQ, USER_ID, RENT_START, RENT_END, DEPOSIT, DEPOSIT_YN, EXTEND_YN, KEY_YN,     
	RENT_MEMO, UPD_DT, UPD_ID, RENT_TYPE, PAY_GUBUN, ORDERNO    
)VALUES(
	#{boxCd}, #{boxNum}, #{rentSeq}, #{userId}, #{rentStart}, #{rentEnd}, #{deposit}, #{depositYn}, #{extendYn}, #{keyYn},
	#{rentMemo}, now(), #{updId}, #{rentType}, #{payGubun}, #{orderno}
)

mapper.select:
SELECT IFNULL(MAX(RENT_SEQ),0)+1
FROM ACM_BOX_RENT
WHERE BOX_CD = #{boxCd} AND BOX_NUM = #{boxNum}

mapper.update:
UPDATE ACM_BOX_NUM 
SET 
	USER_ID    = #{userId},
	BOX_FLAG  = #{boxFlag},

	RENT_SEQ  = #{rentSeq},


	RENT_MEMO = #{rentMemo},

	UPD_DT = now(),
	UPD_ID = #{updId}
WHERE BOX_CD = #{boxCd} 
AND BOX_NUM = #{boxNum}

UPDATE acm_order_approvals SET PRICE = #{payTotal}, PRICE_DISCOUNT = #{priceDiscount}, PRICE_CARD = #{priceCard}, PRICE_CASH = #{priceCash},
PRICE_DISCOUNT_REASON = #{priceDiscountReason},
PAYCODE = #{payGubun} WHERE ORDERNO = #{orderno}

UPDATE ACM_BOX_NUM
SET USER_ID = #{userId}, 
BOX_FLAG = #{boxFlag}, 
RENT_SEQ = #{rentSeq}, 
RENT_MEMO = #{rentMemo}
WHERE BOX_CD = #{boxCd}
AND BOX_NUM = #{boxNum}
