mapper.insert:
INSERT INTO ACM_LEARNING_FORM_INFO (
	FORM_CODE, FORM_NAME, IS_USE, REG_DT, REG_ID, UPD_DT, UPD_ID
) VALUES (
	#{formCode}, #{formName}, #{isUse}, now(), #{regId}, now(), #{updId}
)

mapper.update:
UPDATE ACM_LEARNING_FORM_INFO
SET
	FORM_NAME = #{formName}
	, IS_USE = #{isUse}
	, UPD_DT = now()
	, UPD_ID = #{updId}
WHERE FORM_CODE = #{formCode}

mapper.delete:
UPDATE ACM_LEARNING_FORM_INFO
SET IS_USE = 'N'
WHERE FORM_CODE = #{formCode}
