mapper.select:
SELECT
	LEC_DIV, CODE_NM, FORM_CODE, FORM_NAME, IS_USE, REG_DT, REG_ID, UPD_DT, UPD_ID
FROM ACM_LEARNING_FORM_INFO
WHERE 1 = 1

	AND SYS_CD = #{SEARCHPCODE}


	AND SYS_CD = #{SEARCHCCODE}


	AND IS_USE = #{SEARCHCODEISUSE}

mapper.update:
UPDATE ACM_LEARNING_FORM_INFO
SET
	LEC_DIV = #{LEC_DIV}
	, NAME = #{NAME}
	, ISUSE = #{ISUSE}
	, UPD_DT = SYSDATE
	, UPD_ID = #{UPD_ID}
WHERE CODE = #{CODE}

mapper.delete:
UPDATE ACM_LEARNING_FORM_INFO
SET ISUSE = 'N'
WHERE CODE = #{CODE}
