mapper.select:
SELECT
	SYS_CD, SYS_NM, CODE_NO, CODE_CD, CODE_NM, CODE_VAL, CODE_INFO, ISUSE
FROM TB_BA_CONFIG_CD
WHERE 1 = 1

	AND SYS_CD = #{SEARCHPCODE}


	AND SYS_CD = #{SEARCHCCODE}


	AND ISUSE = #{SEARCHCODEISUSE}

mapper.update:
UPDATE TB_LEARNING_FORM_INFO
SET
	LEC_DIV = #{LEC_DIV}
	, NAME = #{NAME}
	, ISUSE = #{ISUSE}
	, UPD_DT = SYSDATE
	, UPD_ID = #{UPD_ID}
WHERE CODE = #{CODE}

mapper.delete:
UPDATE TB_LEARNING_FORM_INFO
SET ISUSE = 'N'
WHERE CODE = #{CODE}
