mapper.select:
SELECT USER_ID, USER_NM, USER_ROLE, ADMIN_ROLE, PHONE_NO, EMAIL, (SELECT ONOFF_DIV FROM TB_SG_SITE WHERE SITE_ID=ADMIN_ROLE ) ONOFF_DIV, USER_NICKNM FROM TB_MA_MEMBER WHERE USER_ID = #{USERID}
AND USER_PWD = damo.hash_str_data(#{PSWD})
AND USER_ROLE = 'ADMIN'

SELECT ACCESS_DT, USER_IP
FROM (
      SELECT ACCESS_DT, USER_IP,
             RANK () OVER(PARTITION BY USERID ORDER BY ACCESS_DT DESC) RNK
      FROM MB_ACCESS_LOG
      WHERE USERID = #{USERID}
      AND IS_LOGIN = 'Y'

 	  AND IS_LEC_OPEN = 'Y'

      ) AA
WHERE AA.RNK  <  #{LOG_CNT}

AND USER_IP = #{USER_IP}

mapper.insert:
INSERT INTO MB_ACCESS_LOG( ACCESS_DT, USERID, USER_IP, IS_LOGIN
,IS_LEC_OPEN
)VALUES( SYSDATE, #{USERID}, #{USER_IP}, 'Y'
,'Y'
)
