mapper.select:
SELECT * FROM ( SELECT ROWNUM AS RNUM, H.* FROM ( SELECT DISTINCT(G.ORDERNO) ORDERNO, G.ORDERS_USERID, G.ORDERS_USERNAME, G.ORDERS_TELNO, G.ORDERS_CELLNO, G.ORDERS_ZIPCD, G.ORDERS_ADDR, G.ORDERS_EMAIL, G.ORDERS_REGDATE, G.DELIVERS_SENDNO, G.DELIVERS_USERNAME, G.DELIVERS_TELNO, G.DELIVERS_CELLNO, G.DELIVERS_ZIPCD, G.DELIVERS_ADDR, G.DELIVERS_MEMO, G.DELIVERS_REGDATE, G.DELIVERS_SENDDATE, G.APPROVALS_PRICE, G.APPROVALS_ADDPRICE, G.APPROVALS_PAYCODE, G.APPROVALS_ACCTNOCODE, G.APPROVALS_PAYNAME, G.APPROVALS_POINT, G.REFUND_POINT, G.APPROVALS_RETURNVALUE, G.APPROVALS_REGDATE, G.APPROVALS_REPRICE, G.APPROVALS_PAYCODENAME, G.APPROVALS_ACCTNONAME, G.OPEN_ADMIN_ID, (SELECT USER_NM FROM TB_MA_MEMBER WHERE USER_ID = G.OPEN_ADMIN_ID) OPEN_ADMIN_NM, G.ROWCNT, G.SENDDATE, G.BANNER_NAME FROM( SELECT A.ORDERNO, A.USER_ID AS ORDERS_USERID, A.USER_NM AS ORDERS_USERNAME, A.TEL_NO AS ORDERS_TELNO, A.PHONE_NO AS ORDERS_CELLNO, A.ZIP_CODE AS ORDERS_ZIPCD, A.ADDRESS1 AS ORDERS_ADDR, A.EMAIL AS ORDERS_EMAIL, TO_CHAR(A.REG_DT, 'YYYY-MM-DD') AS ORDERS_REGDATE, B.SENDNO AS DELIVERS_SENDNO, B.USERNAME AS DELIVERS_USERNAME, B.TELNO AS DELIVERS_TELNO, B.CELLNO AS DELIVERS_CELLNO, B.ZIPCD AS DELIVERS_ZIPCD, B.ADDR AS DELIVERS_ADDR, B.MEMO AS DELIVERS_MEMO, B.REGDATE AS DELIVERS_REGDATE, B.SENDDATE AS DELIVERS_SENDDATE, C.PRICE AS APPROVALS_PRICE, NVL(C.ADDPRICE,0) AS APPROVALS_ADDPRICE, C.PAYCODE AS APPROVALS_PAYCODE, C.ACCTNOCODE AS APPROVALS_ACCTNOCODE, C.PAYNAME AS APPROVALS_PAYNAME, C.POINT AS APPROVALS_POINT, C.REFUND_POINT AS REFUND_POINT, C.RETURNVALUE AS APPROVALS_RETURNVALUE, C.REG_DT AS APPROVALS_REGDATE, C.REPRICE AS APPROVALS_REPRICE, C.PAYCODE APPROVALS_PAYCODENAME, C.ACCTNOCODE APPROVALS_ACCTNONAME, C.BANNER_NAME, (SELECT OPEN_ADMIN_ID FROM TB_ORDER_MGNT_NO WHERE ORDERNO = A.ORDERNO AND ROWNUM =1) AS OPEN_ADMIN_ID, (SELECT COUNT(*) FROM TB_ORDER_MGNT_NO WHERE ORDERNO = A.ORDERNO
AND STATUSCODE = #{orderstatuscode}
) AS ROWCNT, TO_CHAR(B.SENDDATE, 'YYYY-MM-DD hh24miss') SENDDATE FROM TB_ORDERS A LEFT OUTER JOIN TB_DELIVERS B ON B.ORDERNO = A.ORDERNO LEFT JOIN TB_APPROVALS C ON
C.ORDERNO = B.ORDERNO
C.ORDERNO = B.ORDERNO
C.ORDERNO = A.ORDERNO
LEFT OUTER JOIN TB_ORDER_MGNT_NO D ON D.ORDERNO = A.ORDERNO LEFT OUTER JOIN TB_LEC_MST E ON E.LECCODE = D.MGNTNO LEFT OUTER JOIN TB_CA_BOOK F ON F.RSC_ID = D.MGNTNO WHERE 1=1
AND A.ORDERNO IN (SELECT ORDERNO FROM TB_ORDER_MGNT_NO WHERE ORDERNO = A.ORDERNO AND STATUSCODE = #{orderstatuscode} )
AND C.PAYCODE = #{paycode}
AND TO_CHAR(A.REG_DT, 'YYYYMMDD') BETWEEN #{sdate} AND #{edate}
AND TO_CHAR(B.SENDDATE, 'YYYYMMDD') BETWEEN #{sdate} AND #{edate}
AND INSTR(${searchtype}, #{searchkey}) > 0
AND ( INSTR(A.ORDERNO, #{searchkey}) > 0 OR INSTR(A.USER_ID, #{searchkey}) > 0 OR INSTR(A.USER_NM, #{searchkey}) > 0 OR INSTR(D.MGNTNO, #{searchkey}) > 0
OR INSTR(B.USERNAME, #{SEARCHKEY}) > 0
OR INSTR(B.USERNAME, #{searchkey}) > 0
OR INSTR(C.PAYNAME, #{searchkey}) > 0 )
and c.price = 0 AND D.PTYPE <> 'F' ) G WHERE ROWCNT > 0 ORDER BY ORDERS_REGDATE DESC ) H WHERE ROWNUM <= #{endNo} ) WHERE RNUM > #{startNo}

SELECT * FROM ( SELECT ROWNUM AS RNUM, H.* FROM (
SELECT DISTINCT(G.ORDERNO) ORDERNO, G.ORDERS_USERID, G.ORDERS_USERNAME, G.ORDERS_TELNO, G.ORDERS_CELLNO,G.ORDERS_ZIPCD, G.ORDERS_ADDR, G.ORDERS_EMAIL, G.ORDERS_REGDATE, G.DELIVERS_SENDNO, G.DELIVERS_USERNAME, G.DELIVERS_TELNO, G.DELIVERS_CELLNO, G.DELIVERS_ZIPCD, G.DELIVERS_ADDR, G.DELIVERS_MEMO, G.DELIVERS_REGDATE, G.DELIVERS_SENDDATE, G.APPROVALS_PRICE, G.APPROVALS_ADDPRICE, G.APPROVALS_PAYCODE, G.APPROVALS_ACCTNOCODE, G.APPROVALS_PAYNAME, G.APPROVALS_POINT, G.REFUND_POINT, G.APPROVALS_RETURNVALUE, G.APPROVALS_REGDATE, G.APPROVALS_REPRICE, G.APPROVALS_PAYCODENAME, G.APPROVALS_ACCTNONAME, G.OPEN_ADMIN_ID, (SELECT USER_NM FROM TB_MA_MEMBER WHERE USER_ID = G.OPEN_ADMIN_ID) OPEN_ADMIN_NM, G.ROWCNT, G.SENDDATE, G.BANNER_NAME,G.LECCODE,G.BRIDGE_LECCODE,G.SUBJECT_TITLE,G.CATEGORY_NM,G.SUBJECT_NM,G.SUBJECT_TEACHER_NM, DECODE(G.BANNER_NAME,'M','모바일',G.BANNER_NAME) PC_M_NM ,(SELECT DECODE(PLAYYN,'Y','신청완료','N','수강취소','') FROM TB_MYLECTURE WHERE ORDERNO = G.ORDERNO AND ROWNUM = 1) PLAYYN_NM ,G.FREE_CANCEL_DT FROM( SELECT A.ORDERNO, A.USER_ID AS ORDERS_USERID, A.USER_NM AS ORDERS_USERNAME, A.TEL_NO AS ORDERS_TELNO, A.PHONE_NO AS ORDERS_CELLNO, A.ZIP_CODE AS ORDERS_ZIPCD, A.ADDRESS1 AS ORDERS_ADDR, A.EMAIL AS ORDERS_EMAIL, TO_CHAR(A.REG_DT, 'YYYY-MM-DD hh24:mi:ss') AS ORDERS_REGDATE, B.SENDNO AS DELIVERS_SENDNO, B.USERNAME AS DELIVERS_USERNAME, B.TELNO AS DELIVERS_TELNO, B.CELLNO AS DELIVERS_CELLNO, B.ZIPCD AS DELIVERS_ZIPCD, B.ADDR AS DELIVERS_ADDR, B.MEMO AS DELIVERS_MEMO, B.REGDATE AS DELIVERS_REGDATE, B.SENDDATE AS DELIVERS_SENDDATE, C.PRICE AS APPROVALS_PRICE, NVL(C.ADDPRICE,0) AS APPROVALS_ADDPRICE, C.PAYCODE AS APPROVALS_PAYCODE, C.ACCTNOCODE AS APPROVALS_ACCTNOCODE, C.PAYNAME AS APPROVALS_PAYNAME, C.POINT AS APPROVALS_POINT, C.REFUND_POINT AS REFUND_POINT, C.RETURNVALUE AS APPROVALS_RETURNVALUE, C.REG_DT AS APPROVALS_REGDATE, C.REPRICE AS APPROVALS_REPRICE, C.PAYCODE APPROVALS_PAYCODENAME, C.ACCTNOCODE APPROVALS_ACCTNONAME, C.BANNER_NAME, (SELECT OPEN_ADMIN_ID FROM TB_ORDER_MGNT_NO WHERE ORDERNO = A.ORDERNO AND ROWNUM =1) AS OPEN_ADMIN_ID, (SELECT COUNT(*) FROM TB_ORDER_MGNT_NO WHERE ORDERNO = A.ORDERNO AND STATUSCODE IN ('DLV105','DLV240')
) AS ROWCNT, TO_CHAR(B.SENDDATE, 'YYYY-MM-DD hh24miss') SENDDATE ,E.LECCODE,(SELECT EE.BRIDGE_LECCODE FROM TB_LEC_BRIDGE EE WHERE EE.LECCODE = E.LECCODE) BRIDGE_LECCODE,E.SUBJECT_TITLE, (SELECT NAME FROM TB_CATEGORY_INFO WHERE CODE = E.CATEGORY_CD) AS CATEGORY_NM, (SELECT SUBJECT_NM FROM TB_SUBJECT_INFO WHERE SUBJECT_CD = E.SUBJECT_SJT_CD) AS SUBJECT_NM, (SELECT USER_NM FROM TB_MA_MEMBER WHERE USER_ID = E.SUBJECT_TEACHER) AS SUBJECT_TEACHER_NM , TO_CHAR(D.CANCELDATE, 'YYYY-MM-DD hh24:mi:ss') AS FREE_CANCEL_DT FROM TB_ORDERS A LEFT OUTER JOIN TB_DELIVERS B ON B.ORDERNO = A.ORDERNO LEFT JOIN TB_APPROVALS C ON C.ORDERNO = A.ORDERNO LEFT OUTER JOIN TB_ORDER_MGNT_NO D ON D.ORDERNO = A.ORDERNO LEFT OUTER JOIN TB_LEC_MST E ON E.LECCODE = D.MGNTNO LEFT OUTER JOIN TB_CA_BOOK F ON F.RSC_ID = D.MGNTNO WHERE 1=1
AND A.ORDERNO IN (SELECT ORDERNO FROM TB_MYLECTURE WHERE ORDERNO = A.ORDERNO AND PLAYYN = #{orderstatuscode} )
AND TO_CHAR(A.REG_DT, 'YYYYMMDD') BETWEEN #{sdate} AND #{edate}
AND TO_CHAR(B.SENDDATE, 'YYYYMMDD') BETWEEN #{sdate} AND #{edate}
AND INSTR(${searchtype}, #{searchkey}) > 0
AND ( INSTR(A.ORDERNO, #{searchkey}) > 0 OR INSTR(A.USER_ID, #{searchkey}) > 0 OR INSTR(A.USER_NM, #{searchkey}) > 0 OR INSTR(D.MGNTNO, #{searchkey}) > 0 OR INSTR(E.SUBJECT_TITLE, #{searchkey}) > 0 )
and c.price = 0 AND D.PTYPE = 'F' ) G WHERE ROWCNT > 0 ORDER BY ORDERNO DESC,ORDERS_REGDATE DESC
) H WHERE ROWNUM <= #{endNo} ) WHERE RNUM > #{startNo}

SELECT * FROM ( SELECT ROWNUM AS RNUM, H.* FROM ( SELECT DISTINCT(G.ORDERNO) ORDERNO, G.ORDERS_USERID, G.ORDERS_USERNAME, G.ORDERS_TELNO, G.ORDERS_CELLNO, G.ORDERS_ZIPCD, G.ORDERS_ADDR, G.ORDERS_EMAIL, G.ORDERS_REGDATE, G.DELIVERS_SENDNO, G.DELIVERS_USERNAME, G.DELIVERS_TELNO, G.DELIVERS_CELLNO, G.DELIVERS_ZIPCD, G.DELIVERS_ADDR, G.DELIVERS_MEMO, G.DELIVERS_REGDATE, G.DELIVERS_SENDDATE, G.APPROVALS_PRICE, G.APPROVALS_ADDPRICE, G.APPROVALS_PAYCODE, G.APPROVALS_ACCTNOCODE, G.APPROVALS_PAYNAME, G.APPROVALS_POINT, G.REFUND_POINT, G.APPROVALS_RETURNVALUE, G.APPROVALS_REGDATE, G.APPROVALS_REPRICE, G.APPROVALS_PAYCODENAME, G.APPROVALS_ACCTNONAME,G.STATUSCODE,G.USER_CANCEL, G.OPEN_ADMIN_ID, (SELECT USER_NM FROM TB_MA_MEMBER WHERE USER_ID = G.OPEN_ADMIN_ID) OPEN_ADMIN_NM, G.ROWCNT, G.SENDDATE, G.BANNER_NAME, G.TID,G.USE_DEL_COUPON_CNT,BOOK_SUM FROM( SELECT A.ORDERNO, A.USER_ID AS ORDERS_USERID, A.USER_NM AS ORDERS_USERNAME, A.TEL_NO AS ORDERS_TELNO, A.PHONE_NO AS ORDERS_CELLNO, A.ZIP_CODE AS ORDERS_ZIPCD, A.ADDRESS1 AS ORDERS_ADDR, A.EMAIL AS ORDERS_EMAIL, TO_CHAR(A.REG_DT, 'YYYY-MM-DD') AS ORDERS_REGDATE, A.TID, B.SENDNO AS DELIVERS_SENDNO, B.USERNAME AS DELIVERS_USERNAME, B.TELNO AS DELIVERS_TELNO, B.CELLNO AS DELIVERS_CELLNO, B.ZIPCD AS DELIVERS_ZIPCD, B.ADDR AS DELIVERS_ADDR, B.MEMO AS DELIVERS_MEMO, B.REGDATE AS DELIVERS_REGDATE, B.SENDDATE AS DELIVERS_SENDDATE, C.PRICE AS APPROVALS_PRICE, NVL(C.ADDPRICE,0) AS APPROVALS_ADDPRICE, C.PAYCODE AS APPROVALS_PAYCODE, ( SELECT COUNT (*) FROM TB_TM_MYCOUPON e, TB_TM_COUPON f WHERE e.CCODE = f.CCODE AND f.ADD_FLAG = 'B' AND ORDERNO = A.ORDERNO AND e.ORDERFLAG = 1 AND ROWNUM = 1 ) USE_DEL_COUPON_CNT, (SELECT SUM(PRICE) FROM TB_ORDER_MGNT_NO WHERE ORDERNO = A.ORDERNO AND PTYPE ='L' AND PRICE > 0) AS BOOK_SUM, C.ACCTNOCODE AS APPROVALS_ACCTNOCODE, C.PAYNAME AS APPROVALS_PAYNAME, C.POINT AS APPROVALS_POINT, C.REFUND_POINT AS REFUND_POINT, C.RETURNVALUE AS APPROVALS_RETURNVALUE, C.REG_DT AS APPROVALS_REGDATE, C.REPRICE AS APPROVALS_REPRICE, C.PAYCODE APPROVALS_PAYCODENAME, C.ACCTNOCODE APPROVALS_ACCTNONAME, C.BANNER_NAME, (SELECT STATUSCODE FROM TB_ORDER_MGNT_NO WHERE ORDERNO = A.ORDERNO AND ROWNUM =1) AS STATUSCODE, (SELECT DECODE(USER_CANCEL,'Y','Y','N') FROM TB_ORDER_MGNT_NO WHERE ORDERNO = A.ORDERNO AND ROWNUM =1) AS USER_CANCEL, (SELECT OPEN_ADMIN_ID FROM TB_ORDER_MGNT_NO WHERE ORDERNO = A.ORDERNO AND ROWNUM =1) AS OPEN_ADMIN_ID, (SELECT COUNT(*) FROM TB_ORDER_MGNT_NO WHERE ORDERNO = A.ORDERNO
AND STATUSCODE = #{orderstatuscode}
) AS ROWCNT, TO_CHAR(B.SENDDATE, 'YYYY-MM-DD hh24miss') SENDDATE FROM TB_ORDERS A LEFT OUTER JOIN TB_DELIVERS B ON B.ORDERNO = A.ORDERNO LEFT JOIN TB_APPROVALS C ON
C.ORDERNO = B.ORDERNO
C.ORDERNO = B.ORDERNO
C.ORDERNO = A.ORDERNO
LEFT OUTER JOIN TB_ORDER_MGNT_NO D ON D.ORDERNO = A.ORDERNO LEFT OUTER JOIN TB_LEC_MST E ON E.LECCODE = D.MGNTNO LEFT OUTER JOIN TB_CA_BOOK F ON F.RSC_ID = D.MGNTNO WHERE 1=1
AND D.USER_CANCEL = #{ucancel}
AND A.ORDERNO IN (SELECT ORDERNO FROM TB_ORDER_MGNT_NO WHERE ORDERNO = A.ORDERNO AND STATUSCODE = #{orderstatuscode} )
AND C.PAYCODE = #{paycode}
AND TO_CHAR(A.REG_DT, 'YYYYMMDD') BETWEEN #{sdate} AND #{edate}
AND TO_CHAR(B.SENDDATE, 'YYYYMMDD') BETWEEN #{sdate} AND #{edate}
AND INSTR(${searchtype}, #{searchkey}) > 0
AND ( INSTR(A.ORDERNO, #{searchkey}) > 0 OR INSTR(A.USER_ID, #{searchkey}) > 0 OR INSTR(A.USER_NM, #{searchkey}) > 0 OR INSTR(D.MGNTNO, #{searchkey}) > 0
OR INSTR(B.USERNAME, #{SEARCHKEY}) > 0
OR INSTR(B.USERNAME, #{searchkey}) > 0
OR INSTR(C.PAYNAME, #{searchkey}) > 0 )
and c.price <> 0
and (c.price = 0 or c.price <> 0)
AND D.PTYPE <> 'F' ) G WHERE ROWCNT > 0 ORDER BY ORDERS_REGDATE DESC, ORDERNO DESC ) H WHERE ROWNUM <= #{endNo} ) WHERE RNUM > #{startNo}

select aa.mgntNo, aa.name, aa.cnt, NVL (DECODE (aa.gift_yn, 'Y', 0, aa.price), 0) AS price, nvl(aa.statusCode,'DLV000') as statusCode, aa.wmv_pmp, aa.user_package_flag, aa.confirmDate, aa.confirmDate2, aa.cancelDate, aa.isconfirm, aa.realprice, aa.isCancel, aa.confirmid, aa.gift_yn, DECODE (aa.gift_yn, 'Y', '사은품', '') gift_name, aa.coop_cd from( select b.leccode as mgntNo, b.subject_title as name, a.cnt, a.price, a.statusCode, a.wmv_pmp, a.user_package_flag, decode(a.confirmDate, null, TO_CHAR(SYSDATE , 'YYYY-MM-DD'), '', TO_CHAR(SYSDATE , 'YYYY-MM-DD'), TO_CHAR(a.confirmDate, 'YYYY-MM-DD')) confirmDate, decode(a.confirmDate, null, TO_CHAR(SYSDATE, 'YYYY-MM-DD hh24:mi:ss'), '', TO_CHAR(SYSDATE, 'YYYY-MM-DD hh24:mi:ss'), TO_CHAR(a.confirmDate, 'YYYY-MM-DD hh24:mi:ss')) confirmDate2, a.cancelDate, decode(a.isconfirm, null, TO_CHAR(SYSDATE , 'YYYY-MM-DD'), '', TO_CHAR(SYSDATE , 'YYYY-MM-DD'), TO_CHAR(a.isconfirm, 'YYYY-MM-DD')) isconfirm, (CASE a.wmv_pmp WHEN 'VOD' THEN b.SUBJECT_MOVIE WHEN 'PMP' THEN b.SUBJECT_PMP WHEN 'VOD+PMP' THEN b.SUBJECT_MOVIE_PMP END) AS realprice, a.isCancel, a.confirmid, a.gift_yn, (SELECT COOP_CD FROM TB_LEC_COOP WHERE LECCODE = mgntNo) coop_cd from TB_ORDER_MGNT_NO a, TB_LEC_MST b where a.orderNo = #{orderno} and a.mgntNo = b.leccode
and a.statusCode = #{orderstatuscode}
union all
select b.rsc_id as mgntNo, b.book_nm as name, a.cnt, a.price, a.statusCode, a.wmv_pmp, a.user_package_flag, decode(a.confirmDate, null, TO_CHAR(SYSDATE , 'YYYY-MM-DD'), '', TO_CHAR(SYSDATE , 'YYYY-MM-DD'), TO_CHAR(a.confirmDate, 'YYYY-MM-DD')) confirmDate, decode(a.confirmDate, null, TO_CHAR(SYSDATE, 'YYYY-MM-DD hh24:mi:ss'), '', TO_CHAR(SYSDATE, 'YYYY-MM-DD hh24:mi:ss'), TO_CHAR(a.confirmDate, 'YYYY-MM-DD hh24:mi:ss')) confirmDate2, a.cancelDate, TO_CHAR(a.isconfirm, 'YYYY-MM-DD') isconfirm, b.price as realprice, a.isCancel, a.confirmid, a.gift_yn, (SELECT COOP_CD FROM TB_LEC_COOP WHERE LECCODE = mgntNo) coop_cd from TB_ORDER_MGNT_NO a, TB_CA_BOOK b where a.orderNo = #{orderno} and a.mgntNo = b.rsc_id
and a.statusCode = #{orderstatuscode}
)aa order by aa.mgntNo

