mapper.select:
select USER_ID, USER_NM
FROM TB_ORDERS
WHERE ORDERNO = #{orderno}

select distinct bb.package_no, bb.USERID user_id, bb.orderno, bb.lecture_no, TO_CHAR(bb.START_DATE, 'YYYYMMDD') mylecture_start_date, TO_CHAR(bb.END_DATE, 'YYYYMMDD') mylecture_end_date, bb.PERIOD mylecture_period, cc.subject_period, cc.subject_title package_name, nvl(dd.mylecture_paused_count, 0) as mylecture_paused_count, nvl(dd.mylecture_paused_period, 0) as mylecture_paused_period, nvl(TO_CHAR(dd.mylecture_paused_date1, 'YYYYMMDD'), '') as mylecture_paused_date1, nvl(dd.mylecture_paused_period1, 0) as mylecture_paused_period1, nvl(TO_CHAR(dd.mylecture_paused_date2, 'YYYYMMDD'), '') as mylecture_paused_date2, nvl(dd.mylecture_paused_period2, 0) as mylecture_paused_period2, nvl(TO_CHAR(dd.mylecture_paused_date3, 'YYYYMMDD'), '') as mylecture_paused_date3, nvl(dd.mylecture_paused_period3, 0) as mylecture_paused_period3, nvl(aa.spo,'') spo, (select bridge_leccode from tb_lec_bridge where leccode = bb.lecture_no) bridge_leccode from (select a.USER_ID as user_id, a.ORDERNO, b.mgntNo as package_no, b.wmv_pmp, b.price, b.spo from tb_orders a, tb_order_mgnt_no b where a.orderNo = b.orderNo and b.PTYPE <> 'L' ) aa join TB_MYLECTURE bb on aa.orderno = bb.orderno and aa.package_no = bb.package_no join TB_LEC_MST cc on bb.lecture_no = cc.leccode left outer join TB_MYLECTURE_PAUSE dd on aa.user_id = dd.USER_ID and bb.USERID = dd.user_id and aa.orderNo = dd.orderNo and bb.orderNo = dd.orderNo and aa.package_no = dd.package_no and bb.lecture_no = dd.lecture_no
where bb.USERID = #{userid} and aa.orderno = #{orderno} and bb.package_no = #{package_no}
order by cc.subject_title asc

select count(*)
from tb_order_mgnt_no
where orderNo = #{orderno}
and mgntNo = #{mgntno}
and iscancel = 2

select LEC_TYPE_CHOICE course_type_code
from TB_LEC_MST
where leccode=#{mgntno}

select nvl(refund_point,0) point
from tb_approvals
where orderNo = #{orderno}

select nvl(point, 0) refund_point
from tb_mileage_history
where orderNo = #{orderno}
and userid = #{orders_userid}
and mgntno = #{mgntno}
order by idx desc

Select addprice
From tb_approvals
Where orderNo = #{orderno}

select count(*)
from tb_delivers
where orderNo = #{orderno}

select a.leccode mgntNo, b.sdate, c.orderNo, c.USER_ID userId, '동영상강좌' gbnName, a.subject_title name, NVL (DECODE (b.gift_yn, 'Y', 0, a.subject_price), 0) AS price, a.SUBJECT_DISCOUNT rate, (case b.wmv_pmp when 'VOD' then a.SUBJECT_MOVIE when 'PMP' then a.SUBJECT_PMP when 'VOD+PMP' then a.SUBJECT_MOVIE_PMP end) as realprice, b.cnt, b.statusCode , b.wmv_pmp, NVL (DECODE (b.gift_yn, 'Y', 0, (b.Price * b.cnt) ), 0) AS tot_price, FN_GET_CODE_NM('ORDER_STATUSCODE', (SELECT STATUSCODE FROM TB_ORDER_MGNT_NO WHERE ORDERNO = c.orderno AND ROWNUM = 1)) statusCodeName, b.discountper, b.iscancel, c.TID, (select ROUND(sum(study_percent)/count(*)) from tb_mylecture where orderno = c.orderno and package_no = a.leccode) study_per, b.gift_yn, b.memo from TB_LEC_MST a, TB_ORDER_MGNT_NO b, tb_orders c where a.leccode = b.mgntNo and b.orderNo = c.orderNo and c.orderNo = #{orderno}
union all
select a.rsc_id mgntNo, b.sdate, c.orderNo, c.USER_ID userId, '도서' gbnName, a.book_nm name, NVL (DECODE (b.gift_yn, 'Y', 0, a.price ), 0) AS price, a.discount rate, b.price realprice, b.cnt, b.statusCode , '도서' wmv_pmp,
NVL (DECODE (b.gift_yn, 'Y', 0, (b.Price)), 0) AS tot_price, FN_GET_CODE_NM('ORDER_STATUSCODE', (SELECT STATUSCODE FROM TB_ORDER_MGNT_NO WHERE ORDERNO = c.orderno AND ROWNUM = 1)) statusCodeName, b.discountper, b.iscancel, c.TID, 0 study_per, b.gift_yn, b.memo from TB_CA_BOOK a, TB_ORDER_MGNT_NO b, tb_orders c where a.rsc_id = b.mgntNo and b.orderNo = c.orderNo and c.orderNo = #{orderno}

