SELECT ROWNUM CNT , TO_CHAR(REG_DT, 'YYYY-MM-DD hh24:mi:ss') REG_DT , COUNSELOR_ID , (SELECT USER_NM FROM TB_MA_MEMBER WHERE USER_ID = A.COUNSELOR_ID) COUNSELOR_NM , CS_DIV , (SELECT CODE_NM FROM TB_BA_CONFIG_CD WHERE CODE_VAL = A.CS_DIV AND SYS_CD = 'CSBOARD_GUBUN') CS_DIV_NM , CS_KIND , (SELECT CODE_NM FROM TB_BA_CONFIG_CD WHERE CODE_VAL = A.CS_KIND) CS_KIND_NM , DECODE(ACTION_YN, 'Y', '조치', 'N', '미조치', '') ACTION_YN , CONTENT FROM TB_BOARD_CS A WHERE BOARD_MNG_SEQ = 'CSBOARD_ON' AND REG_ID = #{userid}
AND TO_CHAR(REG_DT, 'YYYYMMDD') between #{sdate} and #{edate}
ORDER BY REG_DT DESC, BOARD_SEQ DESC

SELECT COUNT(*)
FROM TB_BOARD_CS A
WHERE BOARD_MNG_SEQ = 'CSBOARD_ON'
AND REG_ID = #{userid}

	AND TO_CHAR(REG_DT, 'YYYYMMDD') between #{sdate} and #{edate}

SELECT COUNT(*) FROM( SELECT TO_CHAR(SEND_DT, 'YYYY-MM-DD') SEND_DT , '쪽지' STS , CONT , READ_YN FROM TB_NOTE_SEND_INFO WHERE FROM_USERID = #{userid}
AND TO_CHAR(SEND_DT, 'YYYYMMDD') between #{sdate} and #{edate}
UNION ALL
SELECT TO_CHAR(SEND_DT, 'YYYY-MM-DD') SEND_DT , '문자' STS , CONT , READ_YN FROM TB_MAIL_SEND_INFO WHERE FROM_USERID = #{userid}
AND TO_CHAR(SEND_DT, 'YYYYMMDD') between #{sdate} and #{edate}
) A

mapper.update:
UPDATE TB_MA_MEMBER
SET
USER_POINT = #{point}
WHERE USER_ID = #{userid}

MERGE INTO TB_MA_MEMBER_INFO NEW
USING (SELECT #{userid} USER_ID
           FROM DUAL) SRC
     ON (NEW.USER_ID = SRC.USER_ID)
WHEN MATCHED THEN
   UPDATE SET
   REMARK = #{memo}
WHEN NOT MATCHED THEN
    INSERT (
        USER_ID, REMARK
    ) VALUES (
        #{userid}, #{memo}
    )

