SELECT * FROM (SELECT ROWNUM AS RNUM, H.* FROM (SELECT A.ORDERNO, A.ORDER_TYPE , DECODE(A.ORDER_TYPE, 'ON', '온라인신청', 'OFF', '학원방문', '데스크접수') ORDER_TYPE_NM , TO_CHAR(A.REG_DT, 'YYYY-MM-DD') AS REG_DT, ISCONFIRM , A.USER_ID, A.USER_NM, A.PHONE_NO, A.TICKET_PRINT_DT, D.SUBJECT_TITLE, D.LECCODE , AA.CNT SUBJECT_CNT, B.PRICE, C.STATUSCODE , SUBSTR(C.MGNTNO,1,1) MGNTNO , DECODE(C.STATUSCODE, 'DLV105', '결제완료', 'DLV230', '환불완료', 'DLV250', '대기중', '') STATUSCODE_NM , DECODE(B.PAYCODE,'PAY000','',FN_GET_OFF_PAY_NM(A.ORDERNO)) PRICE_STS
FROM (SELECT B.ORDERNO, TO_CHAR(B.ISCONFIRM, 'YYYY-MM-DD') ISCONFIRM, COUNT(C.LECTURE_NO) CNT FROM TB_OFF_ORDERS A, TB_OFF_ORDER_MGNT_NO B, TB_OFF_MYLECTURE C WHERE A.ORDERNO = B.ORDERNO AND A.ORDERNO = C.ORDERNO AND B.MGNTNO = C.PACKAGE_NO AND B.STATUSCODE IN ('DLV105', 'DLV250')
AND TO_CHAR(B.ISCONFIRM, 'YYYYMMDD') BETWEEN #{sdate} AND #{edate}
GROUP BY B.ORDERNO, TO_CHAR(B.ISCONFIRM, 'YYYY-MM-DD')) AA, (SELECT ORDERNO, MAX(STATUSCODE) STATUSCODE, MAX(MGNTNO) MGNTNO FROM TB_OFF_ORDER_MGNT_NO WHERE PTYPE NOT IN('S','T') GROUP BY ORDERNO) C, TB_OFF_ORDERS A, TB_OFF_APPROVALS B, TB_OFF_LEC_MST D WHERE C.ORDERNO = A.ORDERNO(+) AND C.ORDERNO = B.ORDERNO(+) AND C.ORDERNO = AA.ORDERNO(+) AND C.MGNTNO = D.LECCODE
AND A.ORDER_TYPE = #{ordertype}
AND C.STATUSCODE = #{orderstatuscode}
AND B.PAYCODE = #{orderpaycode}
AND REPLACE(ISCONFIRM, '-','') BETWEEN #{sdate} and #{edate}
AND INSTR(REPLACE(${searchtype},' ',''), REPLACE(#{searchkey},' ','')) > 0
AND ( INSTR(REPLACE(A.ORDERNO,' ',''), REPLACE(#{searchkey},' ','')) > 0 OR INSTR(REPLACE(A.USER_NM,' ',''), REPLACE(#{searchkey},' ','')) > 0 OR INSTR(REPLACE(A.USER_ID,' ',''), REPLACE(#{searchkey},' ','')) > 0 OR INSTR(REPLACE(D.SUBJECT_TITLE,' ',''), REPLACE(#{searchkey},' ','')) > 0)
ORDER BY A.REG_DT DESC, A.ORDERNO DESC) H WHERE ROWNUM <= #{endNo} ) WHERE RNUM > #{startNo}

SELECT count(*) FROM (SELECT B.ORDERNO, TO_CHAR(B.ISCONFIRM, 'YYYY-MM-DD') ISCONFIRM, COUNT(C.LECTURE_NO) CNT FROM TB_OFF_ORDERS A, TB_OFF_ORDER_MGNT_NO B, TB_OFF_MYLECTURE C WHERE A.ORDERNO = B.ORDERNO AND A.ORDERNO = C.ORDERNO AND B.MGNTNO = C.PACKAGE_NO AND B.STATUSCODE IN ('DLV105', 'DLV250')
AND TO_CHAR(B.ISCONFIRM, 'YYYYMMDD') BETWEEN #{sdate} AND #{edate}
GROUP BY B.ORDERNO, TO_CHAR(B.ISCONFIRM, 'YYYY-MM-DD')) AA, (SELECT ORDERNO, MAX(STATUSCODE) STATUSCODE, MAX(MGNTNO) MGNTNO FROM TB_OFF_ORDER_MGNT_NO WHERE PTYPE NOT IN('S','T') GROUP BY ORDERNO) C, TB_OFF_ORDERS A, TB_OFF_APPROVALS B, TB_OFF_LEC_MST D WHERE C.ORDERNO = A.ORDERNO(+) AND C.ORDERNO = B.ORDERNO(+) AND C.ORDERNO = AA.ORDERNO(+) AND C.MGNTNO = D.LECCODE
AND A.ORDER_TYPE = #{ordertype}
AND C.STATUSCODE = #{orderstatuscode}
AND B.PAYCODE = #{orderpaycode}
AND REPLACE(ISCONFIRM, '-','') BETWEEN #{sdate} and #{edate}
AND INSTR(REPLACE(${searchtype},' ',''), REPLACE(#{searchkey},' ','')) > 0
AND ( INSTR(REPLACE(A.ORDERNO,' ',''), REPLACE(#{searchkey},' ','')) > 0 OR INSTR(REPLACE(A.USER_NM,' ',''), REPLACE(#{searchkey},' ','')) > 0 OR INSTR(REPLACE(A.USER_ID,' ',''), REPLACE(#{searchkey},' ','')) > 0 OR INSTR(REPLACE(D.SUBJECT_TITLE,' ',''), REPLACE(#{searchkey},' ','')) > 0)

select aa.mgntNo, aa.name, aa.cnt, nvl(aa.price,0) as price, nvl(aa.statusCode,'DLV000') as statusCode, aa.statusCodeNm, aa.wmv_pmp, aa.user_package_flag, aa.confirmDate, aa.confirmDate2, aa.cancelDate, aa.isconfirm, aa.realprice, aa.isCancel, aa.confirmid from( select b.leccode as mgntNo, b.subject_title as name, a.cnt, a.price, a.statusCode, (SELECT NVL(CODE_NM,' ') FROM TB_BA_CONFIG_CD where SYS_CD = 'ORDER_STATUSCODE' and ISUSE = 'Y' and CODE_VAL = a.statusCode) statusCodeNm, a.wmv_pmp, a.user_package_flag, decode(a.confirmDate, null, TO_CHAR(SYSDATE , 'YYYY-MM-DD'), '', TO_CHAR(SYSDATE , 'YYYY-MM-DD'), TO_CHAR(a.confirmDate, 'YYYY-MM-DD')) confirmDate, decode(a.confirmDate, null, TO_CHAR(SYSDATE, 'YYYY-MM-DD hh24:mi:ss'), '', TO_CHAR(SYSDATE, 'YYYY-MM-DD hh24:mi:ss'), TO_CHAR(a.confirmDate, 'YYYY-MM-DD hh24:mi:ss')) confirmDate2, a.cancelDate, decode(a.isconfirm, null, TO_CHAR(SYSDATE , 'YYYY-MM-DD'), '', TO_CHAR(SYSDATE , 'YYYY-MM-DD'), TO_CHAR(a.isconfirm, 'YYYY-MM-DD')) isconfirm, 0 AS realprice, a.isCancel, a.confirmid from TB_OFF_ORDER_MGNT_NO a, TB_OFF_LEC_MST b where a.orderNo = #{orderno} and a.mgntNo = b.leccode
and a.statusCode = #{orderstatuscode}
union all
select b.rsc_id as mgntNo, b.book_nm as name, a.cnt, a.price, a.statusCode, (SELECT NVL(CODE_NM, ' ') FROM TB_BA_CONFIG_CD where SYS_CD = 'ORDER_STATUSCODE' and ISUSE = 'Y' and CODE_VAL = a.statusCode) statusCodeNm, a.wmv_pmp, a.user_package_flag, TO_CHAR(a.confirmDate, 'YYYY-MM-DD') confirmDate, decode(a.confirmDate, null, TO_CHAR(SYSDATE, 'YYYY-MM-DD hh24:mi:ss'), '', TO_CHAR(SYSDATE, 'YYYY-MM-DD hh24:mi:ss'), TO_CHAR(a.confirmDate, 'YYYY-MM-DD hh24:mi:ss')) confirmDate2, a.cancelDate, TO_CHAR(a.isconfirm, 'YYYY-MM-DD') isconfirm, b.price as realprice, a.isCancel, a.confirmid from TB_OFF_ORDER_MGNT_NO a, TB_CA_BOOK b where a.orderNo = #{orderno} and a.mgntNo = b.rsc_id
and a.statusCode = #{orderstatuscode}
)aa order by aa.mgntNo

SELECT ROWNUM RNUM,H.ORDERNO, H.ORDERS_USERID, H.ORDERS_USERNAME || '(' || H.ORDERS_CELLNO || ')' ORDERS_USERNAME, H.ORDERS_TELNO, H.ORDERS_CELLNO, H.ORDERS_ZIPCD, H.ORDERS_ADDR, H.ORDERS_EMAIL, H.ORDER_TYPE, H.TICKET_PRINT_DT, TO_CHAR(H.ORDERS_REGDATE, 'YYYY-MM-DD') ORDERS_REGDATE, H.SUBJECT_REAL_PRICE, H.APPROVALS_PRICE, H.APPROVALS_ADDPRICE, H.APPROVALS_PAYCODE, H.APPROVALS_ACCTNOCODE, H.APPROVALS_PAYNAME, H.APPROVALS_POINT, H.REFUND_POINT, H.APPROVALS_RETURNVALUE, H.APPROVALS_REGDATE, H.SUBJECT_REAL_PRICE, H.APPROVALS_REPRICE, DECODE(H.APPROVALS_PAYCODENAME, 'PAY100', '무통장입금' || H.APPROVALS_ACCTNONAME, 'PAY110', '카드결제' || H.APPROVALS_ACCTNONAME, 'PAY120', '가상계좌' || H.APPROVALS_ACCTNONAME, 'PAY130', '계좌이체' || H.APPROVALS_ACCTNONAME, '없음' || H.APPROVALS_ACCTNONAME) APPROVALS_PAYCODENAME, H.APPROVALS_ACCTNONAME, NVL(H.ACC_BANK_NAME,' ') ACC_BANK_NAME, NVL(H.ACC_BANK_NUM,' ') ACC_BANK_NUM, NVL(H.ACC_BANK_DEPOSITOR,' ') ACC_BANK_DEPOSITOR, H.PRICE_DISCOUNT_REASON, H.MEMO, H.PRICE_STS, H.ROWCNT FROM( SELECT DISTINCT(G.ORDERNO), G.ORDERS_USERID, G.ORDERS_USERNAME, G.ORDERS_TELNO, G.ORDERS_CELLNO, G.ORDERS_ZIPCD, G.ORDERS_ADDR, G.ORDERS_EMAIL, G.ORDER_TYPE, G.TICKET_PRINT_DT, G.ORDERS_REGDATE, G.SUBJECT_REAL_PRICE, G.APPROVALS_PRICE, G.APPROVALS_ADDPRICE, G.APPROVALS_PAYCODE, G.APPROVALS_ACCTNOCODE, G.APPROVALS_PAYNAME, G.APPROVALS_POINT, G.REFUND_POINT, G.APPROVALS_RETURNVALUE, G.APPROVALS_REGDATE, G.APPROVALS_REPRICE, G.APPROVALS_PAYCODENAME, G.APPROVALS_ACCTNONAME, G.ACC_BANK_NAME, G.ACC_BANK_NUM, G.ACC_BANK_DEPOSITOR, G.PRICE_DISCOUNT_REASON, G.MEMO, G.PRICE_STS, G.ROWCNT FROM( SELECT A.ORDERNO, A.USER_ID AS ORDERS_USERID, A.USER_NM AS ORDERS_USERNAME, A.TEL_NO AS ORDERS_TELNO, A.PHONE_NO AS ORDERS_CELLNO, A.ZIP_CODE AS ORDERS_ZIPCD, A.ADDRESS1 AS ORDERS_ADDR, A.EMAIL AS ORDERS_EMAIL, DECODE(A.ORDER_TYPE, 'ON', '온라인신청', 'OFF', '학원방문', '데스크접수') AS ORDER_TYPE, DECODE(A.TICKET_PRINT_DT, NULL, 'N', 'Y') AS TICKET_PRINT_DT, A.REG_DT AS ORDERS_REGDATE, E.SUBJECT_REAL_PRICE, C.PRICE AS APPROVALS_PRICE, NVL(C.ADDPRICE,0) AS APPROVALS_ADDPRICE, C.PAYCODE AS APPROVALS_PAYCODE, C.ACCTNOCODE AS APPROVALS_ACCTNOCODE, C.PAYNAME AS APPROVALS_PAYNAME, C.POINT AS APPROVALS_POINT, C.REFUND_POINT AS REFUND_POINT, C.RETURNVALUE AS APPROVALS_RETURNVALUE, C.REG_DT AS APPROVALS_REGDATE, C.REPRICE AS APPROVALS_REPRICE, C.PAYCODE APPROVALS_PAYCODENAME, C.ACCTNOCODE APPROVALS_ACCTNONAME, P.ACC_BANK_NAME, P.ACC_BANK_NUM, P.ACC_BANK_DEPOSITOR, FN_GET_OFF_PAY_NM(A.ORDERNO) AS PRICE_STS, C.PRICE_DISCOUNT_REASON, C.MEMO, (SELECT COUNT(*) FROM TB_OFF_ORDER_MGNT_NO WHERE ORDERNO = A.ORDERNO
and STATUSCODE = #{orderstatuscode}
) AS ROWCNT FROM TB_OFF_ORDERS A LEFT JOIN TB_OFF_APPROVALS C ON C.ORDERNO = A.ORDERNO LEFT OUTER JOIN TB_OFF_ORDER_MGNT_NO D ON D.ORDERNO = A.ORDERNO LEFT OUTER JOIN TB_OFF_LEC_MST E ON E.LECCODE = D.MGNTNO LEFT OUTER JOIN TB_CA_BOOK F ON F.RSC_ID = D.MGNTNO LEFT OUTER JOIN TB_OFF_REFUND P ON P.ORDER_ID = A.ORDERNO WHERE 1=1
and a.orderNo in ( select orderNo from TB_OFF_ORDER_MGNT_NO where orderNo = a.orderNo and STATUSCODE = #{orderstatuscode} )
and a.ORDER_TYPE = #{paycode}
and TO_CHAR(a.REG_DT, 'YYYYMMDD') between #{sdate} and #{edate}
and ${searchtype} like '%'||#{searchkey}||'%'
and ( a.orderNo like '%'||#{searchkey}||'%' or a.USER_NM like '%'||#{searchkey}||'%' )
AND D.PTYPE NOT IN('S','T') ORDER BY A.ORDERNO ASC ) G ) H

SELECT * FROM ( SELECT ROWNUM AS RNUM, AA.* FROM ( SELECT A.LECCODE, A.CATEGORY_CD, A.LEARNING_CD, A.SUBJECT_SJT_CD, A.SUBJECT_TITLE, A.LEC_SCHEDULE, A.SUBJECT_REAL_PRICE, A.LEC_TYPE_CHOICE, DECODE(A.SUBJECT_TYPE, '1', '실강', '2', '실시간 영상') SUBJECT_TYPE_NM, B.ST_LEC_DATE, B.ED_LEC_DATE, C.NAME CATEGORY_NM, D.NAME LEARNING_NM, E.SUBJECT_NM, F.USER_NM TEACHER_NM, CASE WHEN B.ST_LEC_DATE > TO_CHAR(SYSDATE, 'YYYY-MM-DD') THEN '진행전' WHEN TO_CHAR(SYSDATE, 'YYYY-MM-DD') BETWEEN B.ST_LEC_DATE AND B.ED_LEC_DATE THEN '진행중' ELSE '종강' END LEC_STAT FROM TB_OFF_LEC_MST A, (SELECT LECCODE, MIN(LEC_DATE) ST_LEC_DATE, MAX(LEC_DATE) ED_LEC_DATE FROM TB_OFF_LECTURE_DATE GROUP BY LECCODE) B, TB_CATEGORY_INFO C, TB_LEARNING_FORM_INFO D, TB_SUBJECT_INFO E, TB_MA_MEMBER F WHERE A.LECCODE = B.LECCODE AND A.LEC_TYPE_CHOICE = 'D' AND A.CATEGORY_CD = C.CODE AND A.LEARNING_CD = D.CODE AND A.SUBJECT_SJT_CD = E.SUBJECT_CD AND A.SUBJECT_TEACHER = F.USER_ID ORDER BY A.SUBJECT_OPEN_DATE DESC) AA WHERE AA.LEC_TYPE_CHOICE = 'D'
AND AA.CATEGORY_CD = #{s_cat_cd}
AND AA.LEARNING_CD = #{s_menu_id}
AND AA.SUBJECT_SJT_CD = #{s_sjt_cd}
AND INSTR(AA.TEACHER_NM, #{search_keyword}) > 0
AND AA.LECCODE = #{search_keyword}
AND INSTR(AA.SUBJECT_TITLE, #{search_keyword}) > 0
) H WHERE ROWNUM <= #{endNo} AND RNUM > #{startNo}

