mapper.select:
SELECT (SELECT SUBJECT_TITLE FROM TB_OFF_LEC_MST WHERE LECCODE = A.LECCODE) || '(' || decode((SELECT SUBJECT_TYPE FROM TB_OFF_LEC_MST WHERE LECCODE = A.LECCODE), '1', '실강', '2', '실시간 영상') || ') ' || (select (CASE WHEN TO_CHAR(min(LEC_DATE), 'YYYYMMDD') > TO_CHAR(sysdate, 'YYYYMMDD') THEN '진행전' WHEN TO_CHAR(min(LEC_DATE), 'YYYYMMDD') <= TO_CHAR(sysdate, 'YYYYMMDD') and TO_CHAR(max(LEC_DATE), 'YYYYMMDD') >= TO_CHAR(sysdate, 'YYYYMMDD') THEN '진행중' ELSE '종강' END) from TB_OFF_LECTURE_DATE where LECCODE = A.LECCODE) SUBJECT_TITLE
, (SELECT SUBJECT_REAL_PRICE FROM TB_OFF_LEC_MST WHERE LECCODE = A.LECCODE) SUBJECT_REAL_PRICE , A.LECCODE , A.USER_ID FROM TB_OFF_CC_CART A WHERE A.USER_ID = #{USER_ID}
ORDER BY A.SEQ

