mapper.update:
UPDATE TB_OFF_MYLECTURE
SET
REG_DT = SYSDATE,
PACKAGE_NO = #{PACKAGE_NO},
LECTURE_NO = #{LECTURE_NO},
REALPRICE = #{REAL_PRICE}
WHERE
