AND USERID = #{USER_ID}
AND PACKAGE_NO = #{ORG_PACKAGE_NO}
AND LECTURE_NO = #{ORG_LECTURE_NO}

UPDATE TB_OFF_ORDER_MGNT_NO
SET
MGNTNO = #{MGNTNO}
, PRICE = #{REAL_PRICE}
, ISCONFIRM = TO_DATE(#{ISCONFIRM})
, STATUSCODE = 'DLV105'
WHERE ORDERNO = #{ORDERNO}
and MGNTNO = #{ORG_MGNTNO}

UPDATE TB_OFF_ORDER_MGNT_NO
SET
PRICE = PRICE + #{REAL_PRICE}
, ISCONFIRM = TO_DATE(#{ISCONFIRM})
, STATUSCODE = 'DLV105'
WHERE ORDERNO = #{ORDERNO}
and MGNTNO = #{ORG_MGNTNO}

mapper.select:
SELECT ROWNUM NUM, MGNTNO, DECODE(LECTURE_NO, NULL, MGNTNO, LECTURE_NO) LECTURE_NO, SUBJECT_REAL_PRICE, PRICE, SUBJECT_TITLE, REALPRICE, LEC_TYPE_CHOICE, O_TYPE,GUBUN,SUBJECT_SJT_CD , TO_CHAR(TICKET_PRINT_DT, 'YYYY-MM-DD hh:mm') TICKET_PRINT_DT FROM ( SELECT DISTINCT A.MGNTNO, A.MGNTNO LECTURE_NO, C.SUBJECT_REAL_PRICE, A.PRICE, C.SUBJECT_TITLE, A.PRICE REALPRICE, C.LEC_TYPE_CHOICE, 'M' O_TYPE ,'' SUBJECT_SJT_CD,'' AS GUBUN, D.TICKET_PRINT_DT FROM TB_OFF_ORDER_MGNT_NO A, TB_OFF_MYLECTURE B, TB_OFF_LEC_MST C, TB_OFF_ORDERS D WHERE A.ORDERNO = B.ORDERNO AND A.MGNTNO = B.PACKAGE_NO AND A.MGNTNO = C.LECCODE AND A.ORDERNO = D.ORDERNO AND A.ORDERNO = #{ORDERNO}
AND A.STATUSCODE = 'DLV105'
UNION ALL SELECT A.MGNTNO, B.LECTURE_NO, C.SUBJECT_REAL_PRICE, A.PRICE, C.SUBJECT_TITLE, ABS(B.REALPRICE), C.LEC_TYPE_CHOICE, 'S' O_TYPE ,C.SUBJECT_SJT_CD,(SELECT O.GUBUN FROM TB_OFF_LEC_JONG O WHERE O.LECCODE = A.MGNTNO AND O.MST_LECCODE = B.LECTURE_NO) GUBUN , D.TICKET_PRINT_DT FROM TB_OFF_ORDER_MGNT_NO A, TB_OFF_MYLECTURE B, TB_OFF_LEC_MST C, TB_OFF_ORDERS D WHERE A.ORDERNO = B.ORDERNO AND A.MGNTNO = B.PACKAGE_NO AND B.LECTURE_NO = C.LECCODE AND A.PTYPE IN ('N', 'J', 'P') AND A.ORDERNO = D.ORDERNO AND A.ORDERNO = #{ORDERNO}
AND A.STATUSCODE = 'DLV105'
ORDER BY MGNTNO, LEC_TYPE_CHOICE DESC ) TT

SELECT ORDERNO
FROM TB_OFF_APPROVALS
WHERE ORDERNO = #{ORDERNO}

