mapper.select:
SELECT KK.*, (GUBUN || TO_CHAR(KK.PSORT, '000')) AS PRIORSORT FROM ( SELECT T1.LECCODE, T1.MST_LECCODE, T1.SORT, T1.GUBUN, TBL.SUBJECT_TITLE, TBL.CATEGORY_CD, (SELECT O.NAME FROM TB_CATEGORY_INFO O WHERE O.CODE = TBL.CATEGORY_CD) CATEGORY_NM, DECODE((SELECT count(*) FROM TB_OFF_MYLECTURE G WHERE G.ORDERNO = #{SCH_ORDERNO} AND G.PACKAGE_NO = T1.LECCODE AND G.LECTURE_NO = T1.MST_LECCODE),'0','N','Y') MYLEC_YN, TBL.LEARNING_CD, TBL.SUBJECT_SJT_CD, TBL.SUBJECT_TEACHER, ROW_NUMBER() OVER(PARTITION BY GUBUN ORDER BY SORT) PSORT, (SELECT NAME FROM TB_LEARNING_FORM_INFO WHERE CODE = TBL.LEARNING_CD) AS LEARNING_NM, (SELECT SUBJECT_NM FROM TB_SUBJECT_INFO WHERE SUBJECT_CD = TBL.SUBJECT_SJT_CD) AS SUBJECT_NM, (SELECT USER_NM FROM TB_MA_MEMBER WHERE USER_ID = TBL.SUBJECT_TEACHER) AS SUBJECT_TEACHER_NM ,DECODE(T1.GUBUN,'1','필수','2','선택','') GUBUN_NM FROM TB_OFF_LEC_JONG T1 INNER JOIN TB_OFF_LEC_MST TBL ON T1.MST_LECCODE = TBL.LECCODE WHERE T1.LECCODE = #{LECCODE}
and T1.GUBUN = #{SCH_GUBUN}
and TBL.SUBJECT_SJT_CD = #{SCH_SUBJECT}
AND TBL.LEC_TYPE_CHOICE = 'D' ) KK ORDER BY PRIORSORT ASC

