mapper.select:
SELECT LTBL.* FROM ( SELECT TBL.*, ROWNUM rnum FROM ( SELECT IDX ,VOCCODE ,DECODE(VOCCODE,'A','통화','B','재통화','C','부재중','D','거절','E','번호오류','F','관심회원') VOCCODE_NM ,DUTYCODE ,DECODE(DUTYCODE,'001','9급공무원','002','7급공무원','003','법원/검찰/교정','005','세무/관세','006','소방공무원','007','군무원','008','경찰공무원','999','독서실','') DUTYCODE_NM ,CONTENT ,REQUSERID ,REQUSERNAME ,REGUSERID ,REGUSERNAME ,TO_CHAR(REGDATE,'YYYY-MM-DD HH24:MM') REG_DT ,UPDDATE ,(SELECT TEL_NO FROM TB_MA_MEMBER WHERE USER_ID = A.REQUSERID) TEL_NO ,(SELECT PHONE_NO FROM TB_MA_MEMBER WHERE USER_ID = A.REQUSERID) PHONE_NO FROM TB_TM_BOARD A WHERE 1=1
AND A.DUTYCODE = #{DUTYCODE}
AND A.VOCCODE = #{VOCCODE}
AND A.REGUSERID = #{TMADMINID}
AND A.REGDATE > TO_DATE(#{SDATE})
AND A.REGDATE < TO_DATE(#{EDATE}) + 1
AND A.REGUSERNAME like '%'||#{SEARCHTEXT}||'%'
AND A.REQUSERNAME like '%'||#{SEARCHTEXT}||'%'
AND A.CONTENT like '%'||#{SEARCHTEXT}||'%'
AND (A.REGUSERNAME like '%'||#{SEARCHTEXT}||'%' OR A.REQUSERNAME like '%'||#{SEARCHTEXT}||'%' OR A.CONTENT like '%'||#{SEARCHTEXT}||'%')
ORDER BY A.IDX DESC ) TBL WHERE ROWNUM <= #{endNo} ) LTBL WHERE rnum > #{startNo}

SELECT COUNT(*) FROM TB_TM_BOARD WHERE 1=1
AND DUTYCODE = #{DUTYCODE}
AND VOCCODE = #{VOCCODE}
AND REGUSERID = #{TMADMINID}
AND REGDATE > TO_DATE(#{SDATE})
AND REGDATE < TO_DATE(#{EDATE}) + 1
AND REGUSERNAME like '%'||#{SEARCHTEXT}||'%'
AND REQUSERNAME like '%'||#{SEARCHTEXT}||'%'
AND CONTENT like '%'||#{SEARCHTEXT}||'%'
AND (REGUSERNAME like '%'||#{SEARCHTEXT}||'%' OR REQUSERNAME like '%'||#{SEARCHTEXT}||'%' OR CONTENT like '%'||#{SEARCHTEXT}||'%')

