SELECT LTBL.* FROM ( SELECT TBL.*, ROWNUM rnum FROM ( SELECT B.ORDERNO, C.MGNTNO, D.SUBJECT_TITLE, D.SUBJECT_PRICE, C.WMV_PMP, C.PRICE, B.IPDATE, C.ISCONFIRM, TO_CHAR(C.ISCONFIRM, 'yyyy-MM-dd') PAYDATE, A.REQUSERID, A.REQUSERNAME, A.REGUSERID, A.REGUSERNAME, LASTREGDATE, ONLYUSER.ALLOCDATE AS LASTALLOCDATE, F.USERTYPE FROM ( SELECT USERID , MAX(ALLOCDATE) ALLOCDATE FROM TB_TM_USERS WHERE 1=1
AND USERTYPE IN ('A', 'B', 'C') AND ADMUSERID = #{TMADMINID}
GROUP BY USERID ) ONLYUSER INNER JOIN TB_TM_USERS F ON F.USERID = ONLYUSER.USERID AND F.ALLOCDATE = ONLYUSER.ALLOCDATE LEFT OUTER JOIN ( SELECT TT.REQUSERID , MAX(TT.REGDATE) LASTREGDATE , MIN(TT.REGDATE) FIRSTREGDATE , TT.REGUSERID FROM TB_TM_BOARD TT WHERE 1=1
AND TT.REGDATE > ADD_MONTHS(TO_DATE(#{SDATE}),-1)
AND TT.VOCCODE IN('A','B','F') AND TT.REGUSERID = #{TMADMINID}
GROUP BY TT.REQUSERID ,TT.REGUSERID ) USEIDTABLE ON F.USERID = USEIDTABLE.REQUSERID AND F.ADMUSERID = USEIDTABLE.REGUSERID LEFT OUTER JOIN TB_TM_BOARD A ON A.REQUSERID = USEIDTABLE.REQUSERID AND A.REGDATE = LASTREGDATE LEFT OUTER JOIN TB_ORDERS B ON B.USER_ID = A.REQUSERID LEFT OUTER JOIN TB_ORDER_MGNT_NO C ON C.ORDERNO = B.ORDERNO LEFT OUTER JOIN TB_LEC_MST D ON D.LECCODE = C.MGNTNO WHERE A.VOCCODE IN ('A', 'B', 'F')
AND C.PTYPE IN ('D') AND C.STATUSCODE IN ('DLV105', 'DLV150') AND C.ISCONFIRM > FIRSTREGDATE
AND C.ISCONFIRM < (A.REGDATE + 21)
AND C.PRICE > 0
AND A.REGUSERID = #{TMADMINID}
AND TO_CHAR(C.ISCONFIRM, 'YYYYMMDD') BETWEEN #{SDATE} AND #{EDATE}
AND INSTR(A.REGUSERNAME, #{SEARCHTEXT}) > 0
AND INSTR(A.REQUSERNAME, #{SEARCHTEXT}) > 0
AND A.REGUSERID = #{SEARCHTEXT}
AND A.REQUSERID = #{SEARCHTEXT}
AND (INSTR(A.REGUSERNAME, #{SEARCHTEXT}) > 0 OR INSTR(A.REQUSERNAME, #{SEARCHTEXT}) > 0 OR REGUSERID = #{SEARCHTEXT} OR REQUSERID = #{SEARCHTEXT} )
ORDER BY C.ISCONFIRM DESC, A.REQUSERNAME ASC ) TBL WHERE ROWNUM <= #{endNo} ) LTBL WHERE rnum > #{startNo}

