UNION ALL
SELECT A.SEQ, A.USER_ID, A.LECCODE, A.KIND_TYPE, A.MOVIE_TYPE, A.REGDATE, SUBJECT_TEACHER ,(CASE WHEN A.PACKAGE_PERIOD > 0 THEN '연장' WHEN A.PACKAGE_PERIOD = 0 THEN '재수강' ELSE '' END) REAPPLYNM ,(SELECT SUBSTR(NAME,1,2) FROM TB_LEARNING_FORM_INFO WHERE CODE = B.LEARNING_CD) AS LRNTYPE ,(CASE A.KIND_TYPE WHEN 'D' THEN '단과' WHEN 'J' THEN '종합' WHEN 'N' THEN '선택' WHEN 'P' THEN '패키지' ELSE '단과' END) KDTYPE ,(SELECT USER_NM FROM TB_MA_MEMBER WHERE USER_ID = SUBJECT_TEACHER) SUBJECT_TEACHER_NM ,SUBJECT_TITLE,SUBJECT_PERIOD,0 SUBJECT_DISCOUNT, SUBJECT_PRICE ,A.REPRICE DISCOUNT_PRICE FROM TB_CC_CART A INNER JOIN TB_LEC_MST B ON A.LECCODE = B.LECCODE WHERE A.KIND_TYPE = 'D' AND NVL(A.REPRICE,0) > 0 AND A.USER_ID = #{USER_ID}
UNION ALL
SELECT A.SEQ, A.USER_ID, A.LECCODE, A.KIND_TYPE, A.MOVIE_TYPE, A.REGDATE, SUBJECT_TEACHER , '' REAPPLYNM ,(SELECT SUBSTR(NAME,1,2) FROM TB_LEARNING_FORM_INFO WHERE CODE = B.LEARNING_CD) AS LRNTYPE ,(CASE A.KIND_TYPE WHEN 'D' THEN '단과' WHEN 'J' THEN '종합' WHEN 'N' THEN '선택' WHEN 'C' THEN '선택' WHEN 'P' THEN '패키지' ELSE '단과' END) KDTYPE ,(SELECT USER_NM FROM TB_MA_MEMBER WHERE USER_ID = SUBJECT_TEACHER) SUBJECT_TEACHER_NM ,SUBJECT_TITLE,SUBJECT_PERIOD, SUBJECT_DISCOUNT, SUBJECT_PRICE ,C.SUMPRICE DISCOUNT_PRICE FROM TB_CC_CART A INNER JOIN TB_LEC_MST B ON A.LECCODE = B.LECCODE INNER JOIN (SELECT LECCODE, KIND_TYPE, UPDATE_ID, COUNT(MST_LECCODE) LECCOUNT, SUM(PRICE) SUMPRICE FROM TB_CC_J_CART WHERE KIND_TYPE = 'C' AND UPDATE_ID = #{USER_ID} GROUP BY LECCODE,KIND_TYPE,UPDATE_ID ) C ON A.USER_ID = C.UPDATE_ID AND A.LECCODE = C.LECCODE AND A.KIND_TYPE = C.KIND_TYPE WHERE A.KIND_TYPE = 'C' AND A.USER_ID = #{USER_ID}

mapper.update:
UPDATE TB_TM_BOARD
SET VOCCODE = #{VOCCODE}
	,DUTYCODE = #{DUTYCODE}
	,CONTENT = #{CONTENT}
	,UPDDATE = SYSDATE
WHERE IDX = #{IDX}
	AND REQUSERID = #{REQUSERID}

mapper.select:
		SELECT LTBL.*
		FROM (
				SELECT TBL.*, ROWNUM rnum
		  		FROM (
						SELECT AA.IDX, AA.ORDERNO, AA.MGNTNO, AA.ADMUSERID, AA.ADMUSERNAME, AA.ALLOCDATE, AA.PASSTYPE,
						       AA.USER_ID, AA.USER_NM, AA.PHONE_NO, AA.TEL_NO, AA.SUBJECT_TITLE,
						       TO_CHAR(AA.END_DATE, 'YYYYMMDD') END_DATE
						FROM (
								SELECT A.IDX, A.ORDERNO, A.MGNTNO, A.ADMUSERID, A.ADMUSERNAME, A.ALLOCDATE, A.PASSTYPE,
								       B.USER_ID, B.USER_NM, B.PHONE_NO, B.TEL_NO, C.SUBJECT_TITLE,
								       (SELECT MAX(END_DATE) FROM TB_ORDER_YEARPACKAGE WHERE ORDERNO = A.ORDERNO AND PACKAGE_NO = A.MGNTNO) END_DATE
								FROM TB_TM_PASS_ORDER A, TB_ORDERS B, TB_LEC_MST C
								WHERE A.ORDERNO = B.ORDERNO
								AND A.MGNTNO= C.LECCODE

								AND ( INSTR(B.USER_ID, #{SEARCHTEXT})  >  0
							            OR INSTR(B.USER_NM, #{SEARCHTEXT})  >  0
							            OR INSTR(B.PHONE_NO, #{SEARCHTEXT})  >  0
		 					            OR INSTR(C.SUBJECT_TITLE, #{SEARCHTEXT})  >  0 )


								AND A.PASSTYPE = #{PTYPE}


								AND A.MGNTNO = #{MGNTNO}

						) AA
						WHERE 1 = 1

						AND TO_CHAR(AA.END_DATE, 'YYYYMMDD')  >=  #{SDATE}


						AND TO_CHAR(AA.END_DATE, 'YYYYMMDD')  <=  #{EDATE}

		    			ORDER BY AA.IDX DESC
					) TBL
              	WHERE ROWNUM  <=  #{endNo}
				) LTBL
		WHERE rnum  >  #{startNo}

SELECT COUNT(*) FROM ( SELECT A.IDX, A.ORDERNO, A.MGNTNO, A.ADMUSERID, A.ADMUSERNAME, A.ALLOCDATE, A.PASSTYPE, B.USER_ID, B.USER_NM, B.PHONE_NO, C.SUBJECT_TITLE, (SELECT MAX(END_DATE) FROM TB_ORDER_YEARPACKAGE WHERE ORDERNO = A.ORDERNO AND PACKAGE_NO = A.MGNTNO) END_DATE FROM TB_TM_PASS_ORDER A, TB_ORDERS B, TB_LEC_MST C WHERE A.ORDERNO = B.ORDERNO AND A.MGNTNO= C.LECCODE
AND ( INSTR(B.USER_ID, #{SEARCHTEXT}) > 0 OR INSTR(B.USER_NM, #{SEARCHTEXT}) > 0 OR INSTR(B.PHONE_NO, #{SEARCHTEXT}) > 0 OR INSTR(C.SUBJECT_TITLE, #{SEARCHTEXT}) > 0 )
AND A.PASSTYPE = #{PTYPE}
AND A.MGNTNO = #{MGNTNO}
) AA WHERE 1 = 1
AND TO_CHAR(AA.END_DATE, 'YYYYMMDD') >= #{SDATE}
AND TO_CHAR(AA.END_DATE, 'YYYYMMDD') <= #{EDATE}

SELECT LTBL.* FROM ( SELECT TBL.*, ROWNUM rnum FROM ( SELECT AA.CATEGORY_CD, (SELECT NAME FROM TB_CATEGORY_INFO WHERE CODE = AA.CATEGORY_CD) AS CATEGORY_NM, AA.LECCODE, AA.SUBJECT_TITLE, AA.SUBJECT_PERIOD, NVL(BB.CNT,0) TOT, NVL((SELECT COUNT(ORDERNO) FROM TB_TM_PASS_ORDER WHERE MGNTNO = BB.LECCODE),0) CNT FROM TB_LEC_MST AA, ( SELECT C.LECCODE, COUNT(A.ORDERNO) CNT FROM TB_ORDERS A, TB_ORDER_MGNT_NO B, TB_LEC_MST C, (SELECT A.ORDERNO, B.MGNTNO FROM TB_ORDERS A, TB_ORDER_MGNT_NO B WHERE A.ORDERNO = B.ORDERNO AND B.STATUSCODE IN ('DLV105', 'DLV230') AND B.PTYPE = 'Y' GROUP BY A.ORDERNO, B.MGNTNO HAVING COUNT(*) = 1) P, (SELECT ORDERNO, PACKAGE_NO, TO_CHAR(END_DATE, 'YYYYMMDD') END_DATE FROM TB_ORDER_YEARPACKAGE GROUP BY ORDERNO, PACKAGE_NO, TO_CHAR(END_DATE, 'YYYYMMDD')) Y WHERE A.ORDERNO = B.ORDERNO AND B.MGNTNO = C.LECCODE AND B.ORDERNO = P.ORDERNO AND B.MGNTNO = P.MGNTNO AND B.ORDERNO = Y.ORDERNO AND B.MGNTNO = Y.PACKAGE_NO AND B.STATUSCODE = 'DLV105' AND B.PRICE > 0 AND Y.END_DATE < TO_CHAR(SYSDATE, 'YYYYMMDD') GROUP BY C.LECCODE) BB WHERE AA.LEC_TYPE_CHOICE = 'Y' AND AA.SUBJECT_PERIOD IN ('6', '12')
AND ( INSTR(AA.LECCODE, #{SEARCHTEXT}) > 0 OR INSTR(AA.SUBJECT_TITLE, #{SEARCHTEXT}) > 0 )
AND AA.SUBJECT_PERIOD = #{SUBJECT_PERIOD}
AND AA.CATEGORY_CD = #{CATEGORY_CD}
AND AA.LECCODE = BB.LECCODE(+) ORDER BY AA.LECCODE DESC ) TBL WHERE ROWNUM <= #{endNo} ) LTBL WHERE rnum > #{startNo}

SELECT COUNT(*) FROM TB_LEC_MST AA, ( SELECT C.LECCODE, COUNT(A.ORDERNO) CNT FROM TB_ORDERS A, TB_ORDER_MGNT_NO B, TB_LEC_MST C, (SELECT A.ORDERNO, B.MGNTNO FROM TB_ORDERS A, TB_ORDER_MGNT_NO B WHERE A.ORDERNO = B.ORDERNO AND B.STATUSCODE IN ('DLV105', 'DLV230') AND B.PTYPE = 'Y' GROUP BY A.ORDERNO, B.MGNTNO HAVING COUNT(*) = 1) P, (SELECT ORDERNO, PACKAGE_NO, TO_CHAR(END_DATE, 'YYYYMMDD') END_DATE FROM TB_ORDER_YEARPACKAGE GROUP BY ORDERNO, PACKAGE_NO, TO_CHAR(END_DATE, 'YYYYMMDD')) Y WHERE A.ORDERNO = B.ORDERNO AND B.MGNTNO = C.LECCODE AND B.ORDERNO = P.ORDERNO AND B.MGNTNO = P.MGNTNO AND B.ORDERNO = Y.ORDERNO AND B.MGNTNO = Y.PACKAGE_NO AND B.STATUSCODE = 'DLV105' AND B.PRICE > 0 AND Y.END_DATE < TO_CHAR(SYSDATE, 'YYYYMMDD') GROUP BY C.LECCODE) BB WHERE AA.LEC_TYPE_CHOICE = 'Y' AND AA.SUBJECT_PERIOD IN ('6', '12')
AND ( INSTR(AA.LECCODE, #{SEARCHTEXT}) > 0 OR INSTR(AA.SUBJECT_TITLE, #{SEARCHTEXT}) > 0 )
AND AA.SUBJECT_PERIOD = #{SUBJECT_PERIOD}
AND AA.CATEGORY_CD = #{CATEGORY_CD}
AND AA.LECCODE = BB.LECCODE(+)

SELECT LTBL.* FROM ( SELECT TBL.*, ROWNUM rnum FROM ( SELECT A.ORDERNO, A.USER_ID, A.USER_NM, A.PHONE_NO, A.TEL_NO, C.SUBJECT_TITLE, C.SUBJECT_PERIOD, D.ADMUSERID, D.ADMUSERNAME, D.ALLOCDATE, Y.END_DATE FROM TB_ORDERS A, TB_ORDER_MGNT_NO B, TB_LEC_MST C, TB_TM_PASS_ORDER D, (SELECT A.ORDERNO, B.MGNTNO FROM TB_ORDERS A, TB_ORDER_MGNT_NO B WHERE A.ORDERNO = B.ORDERNO AND B.STATUSCODE IN ('DLV105', 'DLV230') AND B.PTYPE = 'Y' GROUP BY A.ORDERNO, B.MGNTNO HAVING COUNT(*) = 1) P, (SELECT ORDERNO, PACKAGE_NO, TO_CHAR(END_DATE, 'YYYYMMDD') END_DATE FROM TB_ORDER_YEARPACKAGE GROUP BY ORDERNO, PACKAGE_NO, TO_CHAR(END_DATE, 'YYYYMMDD')) Y WHERE A.ORDERNO = B.ORDERNO AND B.MGNTNO = C.LECCODE AND B.ORDERNO = P.ORDERNO AND B.MGNTNO = P.MGNTNO AND B.ORDERNO = Y.ORDERNO AND B.MGNTNO = Y.PACKAGE_NO AND B.STATUSCODE = 'DLV105' AND B.PRICE > 0 AND B.ORDERNO = D.ORDERNO(+) AND B.MGNTNO = D.MGNTNO(+) AND B.MGNTNO = #{MGNTNO}
AND Y.END_DATE BETWEEN #{SDATE} AND #{EDATE}
AND ( INSTR(C.LECCODE, #{SEARCHTEXT}) > 0 OR INSTR(C.SUBJECT_TITLE, #{SEARCHTEXT}) > 0 )
ORDER BY A.ORDERNO, A.USER_ID DESC ) TBL WHERE ROWNUM <= #{endNo} ) LTBL WHERE rnum > #{startNo}

SELECT COUNT(*) FROM TB_ORDERS A, TB_ORDER_MGNT_NO B, TB_LEC_MST C, TB_TM_PASS_ORDER D, (SELECT A.ORDERNO, B.MGNTNO FROM TB_ORDERS A, TB_ORDER_MGNT_NO B WHERE A.ORDERNO = B.ORDERNO AND B.STATUSCODE IN ('DLV105', 'DLV230') AND B.PTYPE = 'Y' GROUP BY A.ORDERNO, B.MGNTNO HAVING COUNT(*) = 1) P, (SELECT ORDERNO, PACKAGE_NO, TO_CHAR(END_DATE, 'YYYYMMDD') END_DATE FROM TB_ORDER_YEARPACKAGE GROUP BY ORDERNO, PACKAGE_NO, TO_CHAR(END_DATE, 'YYYYMMDD')) Y WHERE A.ORDERNO = B.ORDERNO AND B.MGNTNO = C.LECCODE AND B.ORDERNO = P.ORDERNO AND B.MGNTNO = P.MGNTNO AND B.ORDERNO = Y.ORDERNO AND B.MGNTNO = Y.PACKAGE_NO AND B.STATUSCODE = 'DLV105' AND B.PRICE > 0 AND B.ORDERNO = D.ORDERNO(+) AND B.MGNTNO = D.MGNTNO(+) AND B.MGNTNO = #{MGNTNO}
AND Y.END_DATE BETWEEN #{SDATE} AND #{EDATE}
AND ( INSTR(C.LECCODE, #{SEARCHTEXT}) > 0 OR INSTR(C.SUBJECT_TITLE, #{SEARCHTEXT}) > 0 )

SELECT LTBL.* FROM ( SELECT TBL.*, ROWNUM rnum FROM ( SELECT IDX, VOCCODE, DECODE(VOCCODE, 'A', '통화', 'B', '재통화', 'C', '부재중', 'D', '거절', 'E', '번호오류', 'F', '관심회원') VOCCODE_NM , DUTYCODE, (SELECT NAME FROM TB_CATEGORY_INFO WHERE CODE = DUTYCODE) DUTYCODE_NM , CONTENTS, REQUSERID, REQUSERNAME, REGUSERID, REGUSERNAME, TO_CHAR(REGDATE,'YYYY-MM-DD HH24:MM') REG_DT, UPDDATE , (SELECT TEL_NO FROM TB_MA_MEMBER WHERE USER_ID = A.REQUSERID) TEL_NO , (SELECT PHONE_NO FROM TB_MA_MEMBER WHERE USER_ID = A.REQUSERID) PHONE_NO FROM TB_TM_PASS_BOARD A WHERE 1=1
AND A.DUTYCODE = #{DUTYCODE}
AND A.VOCCODE = #{VOCCODE}
AND A.REGUSERID = #{TMADMINID}
AND A.REGDATE > TO_DATE(#{SDATE})
AND A.REGDATE < TO_DATE(#{EDATE}) + 1
AND INSTR(A.REGUSERNAME, #{SEARCHTEXT}) > 0
AND INSTR(A.REQUSERNAME, #{SEARCHTEXT}) > 0
AND INSTR(A.CONTENTS, #{SEARCHTEXT}) > 0
AND (INSTR(A.REGUSERNAME, #{SEARCHTEXT}) > 0 OR INSTR(A.REQUSERNAME, #{SEARCHTEXT}) > 0 OR INSTR(A.CONTENTS, #{SEARCHTEXT}) > 0)
ORDER BY A.IDX DESC ) TBL WHERE ROWNUM <= #{endNo} ) LTBL WHERE rnum > #{startNo}

SELECT COUNT(*) FROM TB_TM_PASS_BOARD WHERE 1=1
AND DUTYCODE = #{DUTYCODE}
AND VOCCODE = #{VOCCODE}
AND REGUSERID = #{TMADMINID}
AND REGDATE > TO_DATE(#{SDATE})
AND REGDATE < TO_DATE(#{EDATE}) + 1
AND INSTR(A.REGUSERNAME, #{SEARCHTEXT}) > 0
AND INSTR(A.REQUSERNAME, #{SEARCHTEXT}) > 0
AND INSTR(A.CONTENTS, #{SEARCHTEXT}) > 0
AND (INSTR(A.REGUSERNAME, #{SEARCHTEXT}) > 0 OR INSTR(A.REQUSERNAME, #{SEARCHTEXT}) > 0 OR INSTR(A.CONTENTS, #{SEARCHTEXT}) > 0)

mapper.delete:
DELETE FROM TB_TM_PASS_BOARD
WHERE IDX = #{IDX}

AND REQUSERID = #{REQUSERID}


AND VOCCODE = #{VOCCODE}

mapper.update:
UPDATE TB_TM_PASS_BOARD
SET CONTENTS = #{CONTENTS}

	, DUTYCODE = #{DUTYCODE}


	, VOCCODE = #{VOCCODE}

WHERE IDX = #{IDX}
